import { builders as b }    from 'ast-types'


export default ({
  name,
  from,
}) => {
  return b.variableDeclaration(
    'const',
    [
      b.variableDeclarator(
        b.identifier(name),
        b.callExpression(
          b.identifier('require'),
          [
            b.stringLiteral(from)
          ]
        )
      )
    ]
  )
}